DROP TABLE EMRS_D_COUNTY_LOCATIONS;
CREATE TABLE MAXDAT_SUPPORT.EMRS_D_COUNTY_LOCATIONS 
   (	COUNTY_CODE VARCHAR2(10),
   	COUNTY VARCHAR2(64) NOT NULL,
   	STATE VARCHAR2(2),
   	LATITUDE NUMBER(6,4),
   	LONGITUDE NUMBER(6,4)
   	)
tablespace MAXDAT_TBS;

--CREATE UNIQUE INDEX "MAXDAT_SUPPORT"."EMRS_D_COUNTY_LOCATIONS_PK" ON "MAXDAT_SUPPORT"."EMRS_D_COUNTY_LOCATIONS" ("COUNTY");

ALTER TABLE "MAXDAT_SUPPORT"."EMRS_D_COUNTY_LOCATIONS" ADD CONSTRAINT "EMRS_D_COUNTY_LOCATIONS_PK" PRIMARY KEY ("COUNTY") USING INDEX  ENABLE;

rem INSERTING into MAXDAT_SUPPORT.EMRS_D_COUNTY_LOCATIONS
SET DEFINE OFF;

COMMIT;

UPDATE EMRS_D_COUNTY_LOCATIONS CL SET COUNTY_CODE = (SELECT COUNTY_CODE FROM EMRS_D_COUNTY_SV CNT WHERE UPPER(CNT.COUNTY_NAME) = UPPER(CL.COUNTY));
COMMIT;

ALTER TABLE "MAXDAT_SUPPORT"."EMRS_D_COUNTY_LOCATIONS" MODIFY ("COUNTY_CODE" NOT NULL ENABLE);

CREATE OR REPLACE VIEW MAXDAT_SUPPORT.EMRS_D_COUNTY_LOCATIONS_SV AS 
SELECT CSDA.CSDA_CODE REGION_CODE
, CSDA.CSDA_NAME REGION_NAME 
, CL.COUNTY_CODE
, CNT.COUNTY_NAME
, CL.STATE
, CL.LATITUDE COUNTY_LATITUDE
, CL.LONGITUDE COUNTY_LONGITUDE
FROM MAXDAT_SUPPORT.EMRS_D_COUNTY_LOCATIONS CL
LEFT JOIN MAXDAT_SUPPORT.EMRS_D_COUNTY_SV CNT ON CNT.COUNTY_CODE = CL.COUNTY_CODE
LEFT JOIN MAXDAT_SUPPORT.EMRS_D_CARE_SERV_DELIV_AREA_SV CSDA ON CSDA.CSDA_CODE = CNT.CSDA_CODE;

GRANT SELECT ON MAXDAT_SUPPORT.EMRS_D_COUNTY_LOCATIONS_SV TO MAXDAT_REPORTS;  
GRANT SELECT ON MAXDAT_SUPPORT.EMRS_D_COUNTY_LOCATIONS_SV TO MAXDATSUPPORT_READ_ONLY;  
 
